export const get = (url) => {
    return fetch(`${process.env.BASE_URL}${url}`).
    then(res => res.json()).
    catch(error => console.error('Error:', error))
}

export const post = (url, params) => {
    return fetch(`${process.env.BASE_URL}${url}`, {
        method: 'POST',
        body: JSON.stringify(params),
        headers: new Headers({
            'Content-Type': 'application/json'
        }),
    }).
    then(res => res.json()).
    catch(error => console.error('Error:', error))
}

export default {
    get,
    post
}
