const request = (url: string, config: any) => {
    return fetch(`${process.env.NEXT_PUBLIC_BASE_URL}${url}`, config).then((res: any) => {
        if (!res.ok) {
            throw Error('');
        }
        return res.json();
    }).then((resJson: any) => {
        if (resJson.code !== 200) {
            throw Error(resJson.message);
        } else {
            return resJson.data;
        }
    }).catch((error: any) => {
        // 公共错误处理
        console.log(error)
    });
};

export const get = (url, params?) => {
    if (params) {
        url += Object.keys(params).reduce((accumulator, currentValue, index) => {
            return `${index !== 0 ? '&' : ''}${accumulator}${currentValue}=${params[currentValue]}`
        }, '?')
    }
    return request(url,{method: 'GET'})
}

export const post = (url, params = {}) => {
    return request(url, {
        body: JSON.stringify(params),
        headers: {
            'content-type': 'application/json'
        },
        method: 'POST'
    });
}

export default {
    get,
    post
}
