const request = (url: string, config: any) => {
    return fetch(url, config).then((res: any) => {
        if (!res.ok) {
            throw Error('');
        }
        return res.json();
    }).then((resJson: any) => {
        // if (xxx) {
        //     // 项目内部认为的错误
        //     throw Error('');
        // } else {
        //     return resJson;
        // }
        console.log(resJson)
        return resJson;
    }).catch((error: any) => {
        // 公共错误处理
        console.log(error)
    });
};

export const get = (url, params?) => {
    url = `${process.env.BASE_URL}${url}`;
    if (params) {
        url += Object.keys(params).reduce((accumulator, currentValue, index) => {
            return `${index !== 0 ? '&' : ''}${accumulator}${currentValue}=${params[currentValue]}`
        }, '?')
    }
    return fetch(url)
        .then(res => res.json())
        .then(({data}) => data)
        .catch(error => console.error('Error:', error))
}

export const post = (url, params = {}) => {
    return fetch(`${process.env.BASE_URL}${url}`, {
        method: 'POST',
        body: JSON.stringify(params),
        headers: new Headers({
            'Content-Type': 'application/json'
        }),
    })
        .then(res => res.json())
        .then(({data}) => data)
        .catch(error => console.error('Error:', error))
}

export default {
    get,
    post
}
